/* HOOKS */
metroActions = {
	events : [],

	exec: function(hook, args){
		for(var i in metroActions.events[hook]){
			metroActions.events[hook][i](args);
		}
	},
	add: function(hooks, func){
		if($.isArray(hooks)){
			for(var i in hooks){
				metroActions.addSingle(hooks[i], func)
			}
		}else{
			metroActions.addSingle(hooks, func)
		}
	},
	addSingle: function(hook, func){
		if(!isset(metroActions.events[hook] )){
			metroActions.events[hook] = [func]
		}else{
			metroActions.events[hook][metroActions.events[hook].length] = func
		}
	}
}

